%----------------------------Rose Diagram Generation---------------------------%fileID = fopen('20B.txt','r');sizeK = [2 Inf];Tho=textscan(fileID,'%f %f','Delimiter',';');x=Tho{1}(isfinite(Tho{1}),:);y=Tho{2}(isfinite(Tho{2}),:);n=length(x);%L = sqrt(abs((diff(x(o:e))^2)-((diff(y(o:e))^2))));for     i=1:1:n/2                o=i+(i-1);        e=i*2;        w(i) = atand((diff(x(o:e)))/(diff(y(o:e))));        wm(i) = (atand((diff(x(o:e)))/(diff(y(o:e))))+180);        endrose((w(:)*pi/180));hold onrose((wm(:)*pi/180));title('Analytical solution of Rose Diagram in Domain I')